    private void BtAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtAgregarActionPerformed
        // TODO add your handling code here:
       escuderia[i] = new Escuderia();
       escuderia[i].setNombreEs(JtNombre.getText());
       escuderia[i].setPaisEsc(JTPais.getText());
       escuderia[i].setPuntos(Integer.parseInt(JtPuntos.getText()));
       escuderia[i].setPosicionTablaConstructore(Integer.parseInt(JtPosicion.getText()));
       
       i++;
       JtNombre.setText("");
       JTPais.setText("");
       JtPuntos.setText("");
       JtPosicion.setText("");
       
        
    }//GEN-LAST:event_BtAgregarActionPerformed

    private void BtEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtEliminarActionPerformed
        // TODO add your handling code here:
         boolean encontrado = false;
        for (int k = 0; k < escuderia.length-1; k++) {
            if (escuderia[k] == null && escuderia[k+1] == null){
                break;
            }
            else if(escuderia[k].getNombreEs().equals(JtNombre.getText())){
                
                escuderia[k] = escuderia[k+1];
                Nota.setText("");
                encontrado = true;
            } 
        }
        if (encontrado == false) {
            Nota.setText("No se encontro");
        }
    }//GEN-LAST:event_BtEliminarActionPerformed

