    private void BtBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtBuscarActionPerformed
        // TODO add your handling code here:
        boolean encontrado = false;
        for (int k = 0; k < escuderia.length-1; k++) {
            if (escuderia[k] == null && escuderia[k+1] == null){
                break;
            }
            else if(escuderia[k].getNombreEs().equals(JtNombre.getText())){
                
                JtNombre.setText(escuderia[k].getNombreEs());
                JTPais.setText(escuderia[k].getPaisEsc());
                JtPuntos.setText(Integer.toString(escuderia[k].getPuntos()));
                JtPosicion.setText(Integer.toString(escuderia[k].getPosicionTablaConstructore()));
                Nota.setText("");
                encontrado = true;
            } 
        }
        if (encontrado == false) {
            Nota.setText("No se encontro");
        }
        
    }//GEN-LAST:event_BtBuscarActionPerformed

